//usuario de la siguiente manera.//Estimado <<nombre>>, el salario de este mes se desglosa de la//siguiente manera.//Salario bruto: #####.##//Deducciones: #####.##//Salario Neto: #####.##String nombre = " " ;String programa;double salario;nombre = JOptionPane.showInputDialog("Digite su nombre"); programa = JOptionPane.showInputDialog("Digite su salario semanal:");salario = Double.parseDouble(programa);double salariomen = salario*4;double deducciones = salariomen * 0.0934;double salariotot = salariomen - deducciones;JOptionPane.showMessageDialog(null,"Estimad@ " +nombre+ ", el salario de este mes se desglosa"+" de la siguiente manera. Salario bruto: " +salariomen+ " Deducciones: " +deducciones+ " Salario Neto: " +salariotot );System.out.println("Estimad@ " +nombre+ ", el salario de este mes se desglosa de la siguiente manera.");System.out.println("Salario bruto: " +salariomen);System.out.println("Deducciones: " +deducciones);System.out.println("Salario Neto: " +salariotot);    }}